# modules/rbac_manager.py - PermissÃµes RBAC entre perfisimport jsonimport osclass RBACManager:    def __init__(self, path="config/permissoes.json"):        self.path = path        self.perfis = {}        self.restritos = ["tunel", "ver_tokens", "root"]        self.carregar_permissoes_json()    def carregar_permissoes_json(self):        if os.path.exists(self.path):            with open(self.path, "r", encoding="utf-8") as f:                self.perfis = json.load(f)        else:            self.perfis = {"admin": [], "user": []}    def salvar_permissoes_em_json(self):        with open(self.path, "w", encoding="utf-8") as f:            json.dump(self.perfis, f, indent=2)    def atribuir_permissao(self, perfil, permissao):        if permissao in self.restritos and perfil != "admin":            return False        if permissao not in self.perfis.get(perfil, []):            self.perfis[perfil].append(permissao)        return True    def remover_permissao(self, perfil, permissao):        if permissao in self.perfis.get(perfil, []):            self.perfis[perfil].remove(permissao)            return True        return False