from TTS.api import TTS# Inicializa o modelo (usa GPU se disponÃ­vel)tts = TTS("tts_models/multilingual/multi-dataset/xtts_v2", gpu=False)def sara_fala(texto, wav_referencia="voz_base_sara.wav", idioma="pt", ficheiro_saida="output_sara.wav"):    tts.tts_to_file(        text=texto,        speaker_wav=wav_referencia,        language=idioma,        file_path=ficheiro_saida    )    return ficheiro_saida