import requests# Interface para comunicaÃ§Ã£o com o modelo Phi-3 via Ollama localdef enviar_para_phi3(prompt: str) -> str:    try:        resposta = requests.post(            "http://localhost:11434/api/generate",            json={"model": "phi3", "prompt": prompt, "stream": False}        )        resposta.raise_for_status()        dados = resposta.json()        return dados.get("response", "Sem resposta.")    except Exception as e:        return f"Erro ao comunicar com phi3: {str(e)}"